package org.everit.json.schema.loader;import org.everit.json.schema.FormatValidator;import java.util.Map;public class FormatValidatorLoader {    private Map<String, FormatValidator> formatValidators;    FormatValidatorLoader(Map<String, FormatValidator> formatValidators) {        this.formatValidators = formatValidators;    }    public FormatValidator loadValidator(String formatName) {        return formatValidators.get(formatName);    }}